mapper.insert:
    insert into t_order (id, 
      order_code, order_state, order_price, activity_id, 
      user_id, vip_code, vip_state, vip_start_time, vip_end_time
      )
    values
		
			(
			#{item.id}, 
      #{item.orderCode}, #{item.orderState}, #{item.orderPrice}, #{item.activityId}, 
      #{item.userId}, #{item.vipCode}, #{item.vipState}, #{item.vipStartTime}, #{item.vipEndTime}
      )

mapper.select:
select 

from t_order


    and order_code = #{orderCode,jdbcType=INTEGER}


    and order_state = #{orderState,jdbcType=INTEGER}


    and order_price = #{orderPrice,jdbcType=DECIMAL}


    and activity_id = #{activityId,jdbcType=INTEGER}


    and user_id = #{userId,jdbcType=INTEGER}


    and vip_code = #{vipCode,jdbcType=VARCHAR}


    and vip_state = #{vipState,jdbcType=INTEGER}


    and vip_start_time = #{vipStartTime,jdbcType=TIMESTAMP}


    and vip_end_time = #{vipEndTime,jdbcType=TIMESTAMP}

select 

from t_order


    and order_code = #{orderCode,jdbcType=INTEGER}


    and order_state = #{orderState,jdbcType=INTEGER}


    and order_price = #{orderPrice,jdbcType=DECIMAL}


    and activity_id = #{activityId,jdbcType=INTEGER}


    and user_id = #{userId,jdbcType=INTEGER}


    and vip_code = #{vipCode,jdbcType=VARCHAR}


    and vip_state = #{vipState,jdbcType=INTEGER}


    and vip_start_time = #{vipStartTime,jdbcType=TIMESTAMP}


    and vip_end_time = #{vipEndTime,jdbcType=TIMESTAMP}
